import React, { useState } from 'react'




function TextForm(props) {
    const handleUpClick = ()=>{
        //console.log("Upper Case Clicked"+text)
        let newText = text.toUpperCase()
        setText(newText)
    }

    const handleChange = (event)=>{
        setText(event.target.value)
    }
    const[text, setText] = useState("Enter Text Here");
   // console.log(text);
  return (
    <>
    <div className='container'>
        <h1>{props.formTitle}</h1>
<div className="mb-3 my-3">
  <textarea className="form-control" onChange={handleChange} id="exampleFormControlTextarea1" value={text} rows="8"></textarea>
</div>
<button onClick={handleUpClick} className='btn btn-primary'>Convert to Upper Case</button>
    </div>
    <div className="container my-3">
        <h1>Your Text Summary</h1>
        <p>{text.split(" ").length} words, {text.length} characters</p>
        <p>{0.008*text.split(" ").length} mins to read the compelete text</p>
        <h2>Preview</h2>
        <p>{text}</p>
    </div>
    </>
  )
}

export default TextForm
